import * as React from 'react';
import { supabase } from './lib/supabase';
import { useAuthStore } from './lib/stores/auth';

// Constants from web app
const INVENTORY_CATEGORIES = [
  'Vegetables', 'Fruits', 'Meat', 'Seafood', 'Dairy', 'Grains',
  'Spices', 'Beverages', 'Cleaning Supplies', 'Other'
];

const UNITS = [
  'kg', 'g', 'lbs', 'oz', 'liters', 'ml', 'pieces', 'boxes', 'cans', 'bottles'
];

const TRANSACTION_REASONS = {
  in: ['purchase', 'delivery'],
  out: ['sale', 'waste', 'transfer']
};

interface InventoryItem {
  id: string;
  name: string;
  category: string;
  unit: string;
  cost_per_unit: number;
  current_stock: number;
  min_threshold: number;
  supplier_id?: string;
  restaurant_id: string;
  created_at: string;
  updated_at: string;
}

interface StockTransaction {
  id: string;
  item_id: string;
  type: 'in' | 'out';
  quantity: number;
  reason: 'purchase' | 'delivery' | 'sale' | 'waste' | 'transfer';
  notes?: string;
  user_id: string;
  restaurant_id: string;
  created_at: string;
}

interface AppState {
  activeTab: string;
  inventory: InventoryItem[];
  transactions: StockTransaction[];
  showAddModal: boolean;
  showAddInventoryModal: boolean;
  showUpdateModal: boolean;
  selectedItem: InventoryItem | null;
  loading: boolean;
  error: string | null;
  showLoginModal: boolean;
}

class App extends React.Component<{}, AppState> {
  constructor(props: {}) {
    super(props);
    this.state = {
      activeTab: 'inventory',
      inventory: [
        {
          id: '1',
          name: 'Fresh Carrots',
          category: 'Vegetables',
          unit: 'kg',
          cost_per_unit: 2.50,
          current_stock: 120,
          min_threshold: 30,
          restaurant_id: 'demo-restaurant',
          created_at: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString(),
          updated_at: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString()
        },
        {
          id: '2',
          name: 'Premium Beef',
          category: 'Meat',
          unit: 'kg',
          cost_per_unit: 25.00,
          current_stock: 45,
          min_threshold: 50,
          restaurant_id: 'demo-restaurant',
          created_at: new Date(Date.now() - 5 * 24 * 60 * 60 * 1000).toISOString(),
          updated_at: new Date(Date.now() - 4 * 60 * 60 * 1000).toISOString()
        },
        {
          id: '3',
          name: 'Whole Milk',
          category: 'Dairy',
          unit: 'liters',
          cost_per_unit: 1.20,
          current_stock: 0,
          min_threshold: 20,
          restaurant_id: 'demo-restaurant',
          created_at: new Date(Date.now() - 3 * 24 * 60 * 60 * 1000).toISOString(),
          updated_at: new Date(Date.now() - 6 * 60 * 60 * 1000).toISOString()
        }
      ],
      transactions: [
        {
          id: 't1',
          item_id: '1',
          type: 'in',
          quantity: 50,
          reason: 'delivery',
          notes: 'Weekly delivery from supplier',
          user_id: 'user-manager',
          restaurant_id: 'demo-restaurant',
          created_at: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString()
        },
        {
          id: 't2',
          item_id: '2',
          type: 'out',
          quantity: 15,
          reason: 'sale',
          notes: 'Used for dinner service',
          user_id: 'user-kitchen',
          restaurant_id: 'demo-restaurant',
          created_at: new Date(Date.now() - 4 * 60 * 60 * 1000).toISOString()
        },
        {
          id: 't3',
          item_id: '3',
          type: 'in',
          quantity: 25,
          reason: 'purchase',
          notes: 'Emergency restock',
          user_id: 'user-staff',
          restaurant_id: 'demo-restaurant',
          created_at: new Date(Date.now() - 6 * 60 * 60 * 1000).toISOString()
        }
      ],
      showAddModal: false,
      showAddInventoryModal: false,
      showUpdateModal: false,
      selectedItem: null,
      loading: false,
      error: null,
      showLoginModal: false
    };
  }

  async componentDidMount() {
    await this.fetchInventoryItems();
    await this.fetchTransactions();
  }

  fetchInventoryItems = async () => {
    this.setState({ loading: true, error: null });
    try {
      const { data, error } = await supabase
        .from('inventory_items')
        .select('*, suppliers(name)')
        .order('name');

      if (error) throw error;
      this.setState({ inventory: data || [], loading: false });
    } catch (error: any) {
      this.setState({ error: error.message, loading: false });
    }
  };

  fetchTransactions = async () => {
    try {
      const { data, error } = await supabase
        .from('stock_transactions')
        .select(`
          *,
          inventory_items(name, unit),
          user_profiles(email)
        `)
        .order('created_at', { ascending: false })
        .limit(100);

      if (error) throw error;
      this.setState({ transactions: data || [] });
    } catch (error: any) {
      this.setState({ error: error.message });
    }
  };

  setActiveTab = (tab: string) => {
    this.setState({ activeTab: tab });
  };

  updateStock = async (itemId: string, quantity: number, type: 'in' | 'out', reason: 'purchase' | 'delivery' | 'sale' | 'waste' | 'transfer', notes?: string) => {
    this.setState({ loading: true, error: null });
    try {
      // Create the stock transaction
      const transactionData = {
        item_id: itemId,
        type,
        quantity,
        reason,
        notes: notes || `Mobile ${type === 'in' ? 'stock in' : 'stock out'}`,
        user_id: 'mobile-user',
        restaurant_id: 'demo-restaurant'
      };

      const { data: transactionResult, error: transactionError } = await supabase
        .from('stock_transactions')
        .insert(transactionData)
        .select(`
          *,
          inventory_items(name, unit),
          user_profiles(email)
        `)
        .single();

      if (transactionError) throw transactionError;

      // Update the inventory item stock
      const item = this.state.inventory.find(i => i.id === itemId);
      if (item) {
        const newStock = type === 'in' ? item.current_stock + quantity : item.current_stock - quantity;
        const { error: updateError } = await supabase
          .from('inventory_items')
          .update({ current_stock: Math.max(0, newStock) })
          .eq('id', itemId);

        if (updateError) throw updateError;
      }

      // Refresh the data
      await this.fetchInventoryItems();
      await this.fetchTransactions();

      this.setState({
        showUpdateModal: false,
        selectedItem: null,
        loading: false
      });
    } catch (error: any) {
      this.setState({ error: error.message, loading: false });
    }
  };

  addNewItem = async (name: string, category: string, unit: string, costPerUnit: number, minThreshold: number, supplierId?: string) => {
    this.setState({ loading: true, error: null });
    try {
      const itemData = {
        name,
        category,
        unit,
        cost_per_unit: costPerUnit,
        current_stock: 0, // New items start with 0 stock, just like web app
        min_threshold: minThreshold,
        supplier_id: supplierId || null,
        restaurant_id: 'demo-restaurant'
      };

      const { data, error } = await supabase
        .from('inventory_items')
        .insert(itemData)
        .select('*, suppliers(name)')
        .single();

      if (error) throw error;

      // Refresh the inventory items
      await this.fetchInventoryItems();

      this.setState({
        showAddInventoryModal: false,
        loading: false
      });
    } catch (error: any) {
      this.setState({ error: error.message, loading: false });
    }
  };

  getStockStatus = (item: InventoryItem) => {
    if (item.current_stock === 0) return { text: 'âŒ Out of Stock', color: '#ef4444' };
    if (item.current_stock <= item.min_threshold) return { text: 'âš ï¸ Low Stock', color: '#f59e0b' };
    return { text: 'âœ… In Stock', color: '#10b981' };
  };

  getCategoryEmoji = (category: string) => {
    const emojiMap: { [key: string]: string } = {
      'Vegetables': 'ðŸ¥•',
      'Fruits': 'ðŸŽ',
      'Meat': 'ðŸ¥©',
      'Seafood': 'ðŸŸ',
      'Dairy': 'ðŸ§€',
      'Grains': 'ðŸŒ¾',
      'Spices': 'ðŸŒ¶ï¸',
      'Beverages': 'ðŸ¥¤',
      'Cleaning Supplies': 'ðŸ§½',
      'Other': 'ðŸ“¦'
    };
    return emojiMap[category] || 'ðŸ“¦';
  };

  formatCurrency = (amount: number) => {
    return `$${amount.toFixed(2)}`;
  };

  formatTimeAgo = (timestamp: string) => {
    const now = new Date();
    const time = new Date(timestamp);
    const diffInHours = Math.floor((now.getTime() - time.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) return 'Just now';
    if (diffInHours === 1) return '1 hour ago';
    return `${diffInHours} hours ago`;
  };

  render() {
    const { activeTab, inventory, transactions, showAddModal, showUpdateModal, selectedItem } = this.state;

    const containerStyle = {
      minHeight: '100vh',
      backgroundColor: '#f5f5f5',
      display: 'flex',
      flexDirection: 'column' as const,
    };

    const headerStyle = {
      backgroundColor: '#2563eb',
      paddingTop: '60px',
      paddingBottom: '20px',
      paddingLeft: '20px',
      paddingRight: '20px',
      textAlign: 'center' as const,
    };

    const titleStyle = {
      fontSize: '24px',
      fontWeight: 'bold',
      color: '#ffffff',
      marginBottom: '4px',
      margin: '0',
    };

    const subtitleStyle = {
      fontSize: '14px',
      color: '#bfdbfe',
      margin: '0',
    };

    const tabContainerStyle = {
      display: 'flex',
      flexDirection: 'row' as const,
      backgroundColor: '#ffffff',
      borderBottom: '1px solid #e5e7eb',
    };

    const tabStyle = {
      flex: 1,
      padding: '15px',
      textAlign: 'center' as const,
      border: 'none',
      borderBottom: '2px solid transparent',
      backgroundColor: 'transparent',
      cursor: 'pointer',
    };

    const activeTabStyle = {
      borderBottomColor: '#2563eb',
    };

    const tabTextStyle = {
      fontSize: '14px',
      color: '#6b7280',
      fontWeight: '500',
    };

    const activeTabTextStyle = {
      color: '#2563eb',
      fontWeight: 'bold',
    };

    const contentStyle = {
      flex: 1,
      padding: '16px',
      overflowY: 'auto' as const,
    };

    const sectionTitleStyle = {
      fontSize: '20px',
      fontWeight: 'bold',
      color: '#1f2937',
      marginBottom: '16px',
      margin: '0 0 16px 0',
    };

    const cardStyle = {
      backgroundColor: '#ffffff',
      padding: '16px',
      marginBottom: '12px',
      borderRadius: '8px',
      boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
    };

    const alertCardStyle = {
      borderLeft: '4px solid #f59e0b',
    };

    const criticalCardStyle = {
      borderLeft: '4px solid #ef4444',
    };

    const cardTitleStyle = {
      fontSize: '16px',
      fontWeight: 'bold',
      color: '#1f2937',
      marginBottom: '4px',
      margin: '0 0 4px 0',
    };

    const cardSubtitleStyle = {
      fontSize: '14px',
      color: '#6b7280',
      marginBottom: '2px',
      margin: '0 0 2px 0',
    };

    const cardStatusStyle = {
      fontSize: '12px',
      fontWeight: '600',
      marginTop: '4px',
      margin: '4px 0 0 0',
    };

    const alertTitleStyle = {
      fontSize: '14px',
      fontWeight: 'bold',
      color: '#1f2937',
      marginBottom: '4px',
      margin: '0 0 4px 0',
    };

    const alertTextStyle = {
      fontSize: '13px',
      color: '#4b5563',
      margin: '0',
    };

    const statusBarStyle = {
      backgroundColor: '#f3f4f6',
      padding: '8px',
      textAlign: 'center' as const,
      borderTop: '1px solid #e5e7eb',
    };

    const statusTextStyle = {
      fontSize: '11px',
      color: '#6b7280',
      margin: '0',
    };

  return (
    <div style={containerStyle}>
      <div style={headerStyle}>
        <h1 style={titleStyle}>ðŸ½ï¸ Restaurant Inventory</h1>
        <p style={subtitleStyle}>
          {user ? `${user.email} (${user.role})` : 'Mobile App - Web Simulation'}
        </p>
      </div>

      <div style={tabContainerStyle}>
        <button
          style={{...tabStyle, ...(activeTab === 'inventory' ? activeTabStyle : {})}}
          onClick={() => this.setActiveTab('inventory')}
        >
          <span style={{...tabTextStyle, ...(activeTab === 'inventory' ? activeTabTextStyle : {})}}>
            ðŸ“¦ Inventory
          </span>
        </button>
        <button
          style={{...tabStyle, ...(activeTab === 'alerts' ? activeTabStyle : {})}}
          onClick={() => this.setActiveTab('alerts')}
        >
          <span style={{...tabTextStyle, ...(activeTab === 'alerts' ? activeTabTextStyle : {})}}>
            ðŸ”” Alerts
          </span>
        </button>
        <button
          style={{...tabStyle, ...(activeTab === 'reports' ? activeTabStyle : {})}}
          onClick={() => this.setActiveTab('reports')}
        >
          <span style={{...tabTextStyle, ...(activeTab === 'reports' ? activeTabTextStyle : {})}}>
            ðŸ“Š Reports
          </span>
        </button>
        <button
          style={{...tabStyle, ...(activeTab === 'tracking' ? activeTabStyle : {})}}
          onClick={() => this.setActiveTab('tracking')}
        >
          <span style={{...tabTextStyle, ...(activeTab === 'tracking' ? activeTabTextStyle : {})}}>
            ðŸ“‹ Tracking
          </span>
        </button>
      </div>

      <div style={contentStyle}>
        {activeTab === 'inventory' && (
          <div>
            <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px'}}>
              <h2 style={sectionTitleStyle}>Current Inventory</h2>
              <button
                style={{
                  backgroundColor: '#2563eb',
                  color: 'white',
                  padding: '8px 16px',
                  borderRadius: '6px',
                  border: 'none',
                  fontSize: '14px',
                  fontWeight: '500',
                  cursor: 'pointer'
                }}
                onClick={() => this.setState({ showAddInventoryModal: true })}
              >
                + Add Item
              </button>
            </div>

            {inventory.map(item => {
              const status = this.getStockStatus(item);
              return (
                <div key={item.id} style={cardStyle}>
                  <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start'}}>
                    <div style={{flex: 1}}>
                      <h3 style={cardTitleStyle}>{this.getCategoryEmoji(item.category)} {item.name}</h3>
                      <div style={{marginBottom: '8px'}}>
