                  <p style={{margin: '0 0 8px 0'}}>No transactions found</p>
                  <p style={{margin: '0', fontSize: '14px'}}>Add your first transaction to get started</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      <div style={statusBarStyle}>
        <p style={statusTextStyle}>
          React Native Web âœ… | Touch Events âœ… | Mobile Features âœ…
        </p>
      </div>
    </div>
  );
  }
}

// Class component wrapper to handle authentication without hooks
interface AuthWrapperState {
  user: any;
  loading: boolean;
  showLogin: boolean;
  email: string;
  password: string;
  loginError: string | null;
}

class AuthenticatedApp extends React.Component<{}, AuthWrapperState> {
  private unsubscribe: (() => void) | null = null;

  constructor(props: {}) {
    super(props);
    this.state = {
      user: null,
      loading: true,
      showLogin: false,
      email: '',
      password: '',
      loginError: null
    };
  }

  async componentDidMount() {
    // Initialize auth and subscribe to changes
    await useAuthStore.getState().initialize();

    // Subscribe to auth store changes
    this.unsubscribe = useAuthStore.subscribe((state) => {
      this.setState({
        user: state.user,
        loading: state.loading
      });
    });

    // Set initial state
    const currentState = useAuthStore.getState();
    this.setState({
      user: currentState.user,
      loading: currentState.loading
    });
  }

  componentWillUnmount() {
    if (this.unsubscribe) {
      this.unsubscribe();
    }
  }

  handleLogin = async () => {
    try {
      this.setState({ loginError: null });
      await useAuthStore.getState().signIn(this.state.email, this.state.password);
      this.setState({ showLogin: false });
    } catch (error: any) {
      this.setState({ loginError: error.message });
    }
  };

  handleDemoLogin = () => {
    this.setState({ email: 'manager@demo.com', password: 'demo123' }, () => {
      this.handleLogin();
    });
  };

  render() {
    const { user, loading, showLogin, email, password, loginError } = this.state;

    if (loading) {
      return (
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minHeight: '100vh',
          backgroundColor: '#f9fafb',
          fontFamily: 'system-ui, -apple-system, sans-serif'
        }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{
              width: '40px',
              height: '40px',
              border: '4px solid #e5e7eb',
              borderTop: '4px solid #3b82f6',
              borderRadius: '50%',
              animation: 'spin 1s linear infinite',
              margin: '0 auto 16px'
            }}></div>
            <p style={{ color: '#6b7280', margin: 0 }}>Loading...</p>
          </div>
        </div>
      );
    }

    if (!user) {
      return (
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minHeight: '100vh',
          backgroundColor: '#f9fafb',
          fontFamily: 'system-ui, -apple-system, sans-serif'
        }}>
          <div style={{
            backgroundColor: '#ffffff',
            padding: '32px',
            borderRadius: '12px',
            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
            width: '100%',
            maxWidth: '400px',
            margin: '16px'
          }}>
            <h1 style={{
              fontSize: '24px',
              fontWeight: 'bold',
              textAlign: 'center',
              marginBottom: '24px',
              color: '#1f2937'
            }}>
              ðŸ½ï¸ Restaurant Inventory
            </h1>

            {!showLogin ? (
              <div style={{ textAlign: 'center' }}>
                <p style={{ color: '#6b7280', marginBottom: '24px' }}>
                  Sign in to manage your restaurant inventory
                </p>

                <button
                  onClick={this.handleDemoLogin}
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    backgroundColor: '#3b82f6',
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    fontSize: '16px',
                    fontWeight: '500',
                    cursor: 'pointer',
                    marginBottom: '12px'
                  }}
                >
                  ðŸš€ Try Demo (Manager)
                </button>

                <button
                  onClick={() => this.setState({ showLogin: true })}
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    backgroundColor: 'transparent',
                    color: '#3b82f6',
                    border: '2px solid #3b82f6',
                    borderRadius: '8px',
                    fontSize: '16px',
                    fontWeight: '500',
                    cursor: 'pointer'
                  }}
                >
                  Sign In with Email
                </button>
              </div>
            ) : (
              <div>
                <div style={{ marginBottom: '16px' }}>
                  <label style={{
                    display: 'block',
                    fontSize: '14px',
                    fontWeight: '500',
                    color: '#374151',
                    marginBottom: '6px'
                  }}>
                    Email
                  </label>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => this.setState({ email: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: '2px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '16px',
                      boxSizing: 'border-box'
                    }}
                    placeholder="manager@demo.com"
                  />
                </div>

                <div style={{ marginBottom: '20px' }}>
                  <label style={{
                    display: 'block',
                    fontSize: '14px',
                    fontWeight: '500',
                    color: '#374151',
                    marginBottom: '6px'
                  }}>
                    Password
                  </label>
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => this.setState({ password: e.target.value })}
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: '2px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '16px',
                      boxSizing: 'border-box'
                    }}
                    placeholder="demo123"
                  />
                </div>

                {loginError && (
                  <div style={{
                    backgroundColor: '#fee2e2',
                    color: '#dc2626',
                    padding: '12px',
                    borderRadius: '8px',
                    marginBottom: '16px',
                    fontSize: '14px'
                  }}>
                    {loginError}
                  </div>
                )}

                <button
                  onClick={this.handleLogin}
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    backgroundColor: '#3b82f6',
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    fontSize: '16px',
                    fontWeight: '500',
                    cursor: 'pointer',
                    marginBottom: '12px'
                  }}
                >
                  Sign In
                </button>

                <button
                  onClick={() => this.setState({ showLogin: false })}
                  style={{
                    width: '100%',
                    padding: '12px 16px',
                    backgroundColor: 'transparent',
                    color: '#6b7280',
                    border: '2px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '16px',
                    fontWeight: '500',
                    cursor: 'pointer'
                  }}
                >
                  Back
                </button>

                <div style={{
                  marginTop: '16px',
                  padding: '12px',
                  backgroundColor: '#f3f4f6',
                  borderRadius: '8px',
                  fontSize: '12px',
                  color: '#6b7280'
                }}>
                  <strong>Demo Credentials:</strong><br />
                  Manager: manager@demo.com / demo123<br />
                  Staff: staff@demo.com / demo123
                </div>
              </div>
            )}
          </div>

          <style dangerouslySetInnerHTML={{
            __html: `
              @keyframes spin {
                0% { transform: rotate(0deg); }
                100% { transform: rotate(360deg); }
              }
            `
          }} />
        </div>
      );
    }

    return <App user={user} />;
  }
}

export default AuthenticatedApp;
